// face the popup message box //
function fadeMsgp(flag)
{
  if(flag == null)
  {
    flag = 1;
  }

  var msg = document.getElementById('pmsg');
  var value;
  if(flag == 1)
  {
    value = msg.alpha + MSGSPEED;
  }
  else
  {
    value = msg.alpha - MSGSPEED;
  }

  msg.alpha = value;
  msg.style.opacity = (value / 100);
  msg.style.filter = 'alpha(opacity=' + value + ')';
  if(value >= 99)
  {
    clearInterval(msg.timer);
    msg.timer = null;
  }
  else if(value <= 1)
  {
    msg.style.display = "none";
    clearInterval(msg.timer);
  }
}

// hide the form popup alert //
function hideMsgp(msg)
{
  var msg = document.getElementById('pmsg');
  if(!msg.timer)
  {
    msg.timer = setInterval("fadeMsgp(0)", MSGTIMER);
  }
  
  if (msg == undefined) {
    jQuery('#pmsg').timer = setInterval("fadeMsgp(0)", MSGTIMER);
  }
}


